class PlantSearch {
    constructor(options = {}) {
        // 默认配置
        const defaults = {
            plantData: {
                '银杏': '🌳 学名：Ginkgo biloba\n🏷 保护等级：国家一级\n📐 特征：扇形叶片，雌雄异株\n📍 分布：全国多地人工栽培',
                '水杉': '🌲 学名：Metasequoia glyptostroboides\n🏷 保护等级：国家一级\n📐 特征：落叶乔木，羽状复叶\n📍 分布：湖北利川',
                // 更多数据...
            },
            searchIconClass: 'search-icon',
            resultBoxClass: 'search-result-box',
            inputClass: 'search-input',
            btnClass: 'search-btn'
        };

        this.config = {...defaults, ...options};
        this.init();
    }

    init() {
        // 创建搜索框结构
        document.body.insertAdjacentHTML('beforeend', `
            <div class="search-overlay"></div>
            <div class="search-container">
                <div class="search-box">
                    <input type="text" class="${this.config.inputClass}" placeholder="输入植物名称/学名...">
                    <button class="${this.config.btnClass}">立即搜索</button>
                </div>
                <div class="search-close-btn">×</div>
            </div>
            <div class="${this.config.resultBoxClass}"></div>
        `);

        // 获取元素
        this.searchIcon = document.querySelector(`.${this.config.searchIconClass}`);
        this.overlay = document.querySelector('.search-overlay');
        this.searchContainer = document.querySelector('.search-container');
        this.closeBtn = document.querySelector('.search-close-btn');
        this.searchInput = document.querySelector(`.${this.config.inputClass}`);
        this.searchBtn = document.querySelector(`.${this.config.btnClass}`);
        this.resultBox = document.querySelector(`.${this.config.resultBoxClass}`);

        // 绑定事件
        this.bindEvents();
    }

    bindEvents() {
        this.searchIcon?.addEventListener('click', () => this.showSearch());
        this.overlay?.addEventListener('click', () => this.hideSearch());
        this.closeBtn?.addEventListener('click', () => this.hideSearch());
        this.searchBtn?.addEventListener('click', () => this.handleSearch());

        document.addEventListener('keydown', (e) => {
            if (e.key === 'Enter') this.handleSearch();
            if (e.key === 'Escape') this.hideSearch();
        });

        this.resultBox?.addEventListener('wheel', (e) => e.stopPropagation());
    }

    showSearch() {
        this.overlay.style.display = 'block';
        this.searchContainer.classList.add('active');
        this.searchInput.focus();
    }

    hideSearch() {
        this.overlay.style.display = 'none';
        this.searchContainer.classList.remove('active');
        this.resultBox.classList.remove('show');
        this.searchInput.value = '';
    }

    showResult(text) {
        this.resultBox.textContent = text;
        this.resultBox.classList.add('show');
    }

    handleSearch() {
        const keyword = this.searchInput.value.trim().toLowerCase();
        if (!keyword) return;

        const results = Object.entries(this.config.plantData)
            .filter(([name, desc]) =>
                name.toLowerCase().includes(keyword) ||
                desc.toLowerCase().includes(keyword)
                // name.toLowerCase().includes(keyword)
            );

        let resultText = '';
        if (results.length > 0) {
            results.forEach(([name, desc], index) => {
                resultText += `${index + 1}. ${desc}\n\n`;
            });
        } else {
            resultText = `⚠️  "${keyword}" 不处于国家重点保护野生植物行列\n\n推荐搜索：\n水韭、苏铁、银杏、西藏柏木、水松、水杉等国家重点保护野生植物。`;
        }

        this.showResult(resultText.trim());
    }
}

